mapper.select:
SELECT *
FROM MEMBER
WHERE USER_ID = #{userId}
AND USER_PWD = #{userPwd}

SELECT USER_PWD
FROM MEMBER
WHERE USER_ID = #{userId}

mapper.update:
UPDATE MEMBER 
SET USER_NM = #{userNm}
,USER_PWD = #{userPwd}
,NICKNAME = #{nickname}
,PHONE = #{phone}
WHERE USER_ID = #{userId}

UPDATE MEMBER 
SET LEAVE_DT = SYSDATE
,STATUS = 1
WHERE USER_PWD = #{userPwd}
AND USER_ID = #{userId}

UPDATE MEMBER
SET REMAIN_PT = #{remain}
WHERE USER_NO = #{userNo}
AND STATUS = 0

UPDATE MEMBER
SET REMAIN_PT = #{remain}
WHERE USER_NO = #{userNo}
AND STATUS = 0

