mapper.select:
SELECT *
FROM VIDEO
WHERE USER_NO = #{userNo}
AND V_NO = #{vNo}

mapper.delete:
DELETE FROM VIDEO_LIKE
WHERE USER_NO = #{userNo}
AND V_NO = #{vNo}
AND CH_NO = #{chNo}
AND V_TY = '좋아요'

DELETE FROM VIDEO_LIKE
WHERE USER_NO = #{userNo}
AND V_NO = #{vNo}
AND CH_NO = #{chNo}
AND V_TY = '싫어요'

DELETE FROM VIDEO_LIKE
WHERE USER_NO = #{userNo}
AND V_NO = #{vNo}
AND CH_NO = #{chNo}
AND V_TY = '북마크'

DELETE FROM SUBSCRIBE
WHERE USER_NO = #{loginUser}
AND CH_NO = #{chNo}

mapper.insert:
INSERT INTO REPORT
VALUES(RE_NO.NEXTVAL, #{loginUser}, #{userNo}, SYSDATE, #{check}, '동영상', #{vNo}
, NULL, #{chNm}, #{rCount} + 1, #{cCount} + 1)

mapper.delete:
DELETE FROM ALRAM
WHERE USER_NO = #{userNo}
AND CH_NO = #{chNo}
AND AL_CT LIKE #{nickName}||'%'

mapper.select:
SELECT NVL(SUP_NO, 0)
FROM SUPPORT
WHERE SUP_TY = '정기'
AND SYSDATE BETWEEN SUP_STDT AND REG_EDT
AND USER_NO = #{loginUser}
AND CH_NO = #{chNo}

SELECT NVL(LIKE_NO, 0)
FROM VIDEO_LIKE
WHERE V_NO = #{vNo}
AND USER_NO = #{loginUser} 
AND V_TY = '좋아요'

SELECT LIKE_NO
FROM VIDEO_LIKE
WHERE V_NO = #{vNo}
AND USER_NO = #{loginUser} 
AND V_TY = '싫어요'

SELECT LIKE_NO
FROM VIDEO_LIKE
WHERE V_NO = #{vNo}
AND USER_NO = #{loginUser} 
AND V_TY = '북마크'

mapper.update:
UPDATE VIDEO
SET COUNT = COUNT + 1
WHERE V_NO = #{vNo}

UPDATE LOGER
SET SUBNUM = SUBNUM + 1
WHERE CH_NO = #{chNo}

UPDATE LOGER
SET SUBNUM = SUBNUM - 1
WHERE CH_NO = #{chNo}
